    document.getElementById('zoom2').style.transform="scale(1.2,1.2)"
    document.getElementById('zoom2').style.border="2px solid red"
    document.getElementById('zoom2').style.maxWidth="20"
  }
  // const handl


  return (
    <div className="App" >

      <br/>
      <header className="App-header">
        <h1>CricGif Development Task</h1>
        {/* <img src={logo} className="App-logo" alt="logo" /> */}


      <div id='zoom' style={{display:'flex',justifyContent:'center',alignContent:'center',margin:'0 auto'}}>

        <video id='zoom2' onMouseEnter={handle} autoplay loop muted playsInline pla style={{maxWidth:'40%',maxHeight:'20%',overflow:'hidden'}}>
          <source src="https://storage.googleapis.com/cricgif-bucket-testing1/Singer%20-%2035867.mp4" type="video/mp4" />
        </video>

      </div>
      </header>
    </div>





  );
}

export default App;
